mapper.select:
select a.assign_no, a.lecture_no, a.assign_name, a.end_date, l.lecture_name as lecture_name, u.user_name as teacher_name, floor(datediff(a.create_date, min(a.create_date) over(partition by a.lecture_no)) / 7) + 1 as week_no from assignment a inner join lecture_list l_l on a.lecture_no = l_l.lecture_no inner join lecture l on a.lecture_no = l.lecture_no inner join user u on l.id = u.id where l_l.id = #{ id }
and a.lecture_no = #{ lecture_no }
and a.delete_date is null order by a.create_date asc

mapper.update:
update report set
report_note = #{ report_note } 
where report_no = #{ report_no }

update question set
quest_note = #{ quest_note }
where quest_no = #{ quest_no }
